contracts/LearnHubToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title LearnHubToken
 * @dev ERC20 token for LearnHub Learn-to-Earn system
 * Token Symbol: LHT
 */
contract LearnHubToken is ERC20, ERC20Burnable, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    uint256 public constant MAX_SUPPLY = 1000000000 * 10**18; // 1 billion tokens

    event TokensMinted(address indexed to, uint256 amount, string activityType);
    event TokensBurned(address indexed from, uint256 amount, string reason);

    constructor() ERC20("LearnHub Token", "LHT") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(BURNER_ROLE, msg.sender);
    }

    /**
     * @dev Mint tokens to an address (only minter role)
     * @param to Address to receive tokens
     * @param amount Amount of tokens to mint
     * @param activityType Type of activity that earned the reward
     */
    function mint(
        address to,
        uint256 amount,
        string memory activityType
    ) public onlyRole(MINTER_ROLE) {
        require(to != address(0), "Cannot mint to zero address");
        require(totalSupply() + amount <= MAX_SUPPLY, "Exceeds max supply");
        
        _mint(to, amount);
        emit TokensMinted(to, amount, activityType);
    }

    /**
     * @dev Burn tokens from an address (only burner role)
     * @param from Address to burn tokens from
     * @param amount Amount of tokens to burn
     * @param reason Reason for burning
     */
    function burnFromWithReason(
        address from,
        uint256 amount,
        string memory reason
    ) public onlyRole(BURNER_ROLE) {
        require(from != address(0), "Cannot burn from zero address");
        
        _burn(from, amount);
        emit TokensBurned(from, amount, reason);
    }

    /**
     * @dev Override burn function to include reason
     */
    function burn(uint256 amount) public override {
        _burn(msg.sender, amount);
        emit TokensBurned(msg.sender, amount, "User burn");
    }
}

contracts/RewardDistribution.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./LearnHubToken.sol";

/**
 * @title RewardDistribution
 * @dev Contract to manage reward distribution for learning activities
 */
contract RewardDistribution is AccessControl {
    bytes32 public constant REWARDER_ROLE = keccak256("REWARDER_ROLE");

    LearnHubToken public token;

    // Reward limits per activity type
    mapping(string => uint256) public maxRewardPerActivity;
    mapping(string => bool) public activityEnabled;

    // Track rewards per user per activity to prevent double rewards
    mapping(address => mapping(string => mapping(uint256 => bool))) public hasReceivedReward;

    event RewardDistributed(
        address indexed student,
        string activityType,
        uint256 activityId,
        uint256 amount,
        uint256 timestamp
    );

    event ActivityConfigUpdated(string activityType, uint256 maxReward, bool enabled);

    constructor(address _tokenAddress) {
        require(_tokenAddress != address(0), "Invalid token address");
        token = LearnHubToken(_tokenAddress);
        
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(REWARDER_ROLE, msg.sender);

        // Initialize default reward limits
        maxRewardPerActivity["lesson"] = 10 * 10**18; // 10 LHT
        maxRewardPerActivity["quiz"] = 50 * 10**18; // 50 LHT
        maxRewardPerActivity["assignment"] = 100 * 10**18; // 100 LHT
        maxRewardPerActivity["course"] = 500 * 10**18; // 500 LHT
        maxRewardPerActivity["review"] = 5 * 10**18; // 5 LHT
        maxRewardPerActivity["forum"] = 5 * 10**18; // 5 LHT
        maxRewardPerActivity["streak"] = 50 * 10**18; // 50 LHT

        // Enable all activities by default
        activityEnabled["lesson"] = true;
        activityEnabled["quiz"] = true;
        activityEnabled["assignment"] = true;
        activityEnabled["course"] = true;
        activityEnabled["review"] = true;
        activityEnabled["forum"] = true;
        activityEnabled["streak"] = true;
    }

    /**
     * @dev Distribute reward for completing a lesson
     */
    function distributeLessonReward(
        address student,
        uint256 lessonId,
        uint256 amount
    ) public onlyRole(REWARDER_ROLE) {
        require(activityEnabled["lesson"], "Lesson rewards disabled");
        require(amount <= maxRewardPerActivity["lesson"], "Amount exceeds max reward");
        require(!hasReceivedReward[student]["lesson"][lessonId], "Already rewarded for this lesson");

        hasReceivedReward[student]["lesson"][lessonId] = true;
        token.mint(student, amount, "lesson");

        emit RewardDistributed(student, "lesson", lessonId, amount, block.timestamp);
    }

    /**
     * @dev Distribute reward for completing a quiz
     */
    function distributeQuizReward(
        address student,
        uint256 quizId,
        uint256 amount
    ) public onlyRole(REWARDER_ROLE) {
        require(activityEnabled["quiz"], "Quiz rewards disabled");
        require(amount <= maxRewardPerActivity["quiz"], "Amount exceeds max reward");
        require(!hasReceivedReward[student]["quiz"][quizId], "Already rewarded for this quiz");

        hasReceivedReward[student]["quiz"][quizId] = true;
        token.mint(student, amount, "quiz");

        emit RewardDistributed(student, "quiz", quizId, amount, block.timestamp);
    }

    /**
     * @dev Distribute reward for completing a course
     */
    function distributeCourseReward(
        address student,
        uint256 courseId,
        uint256 amount
    ) public onlyRole(REWARDER_ROLE) {
        require(activityEnabled["course"], "Course rewards disabled");
        require(amount <= maxRewardPerActivity["course"], "Amount exceeds max reward");
        require(!hasReceivedReward[student]["course"][courseId], "Already rewarded for this course");

        hasReceivedReward[student]["course"][courseId] = true;
        token.mint(student, amount, "course");

        emit RewardDistributed(student, "course", courseId, amount, block.timestamp);
    }

    /**
     * @dev Distribute reward for submitting an assignment
     */
    function distributeAssignmentReward(
        address student,
        uint256 assignmentId,
        uint256 amount
    ) public onlyRole(REWARDER_ROLE) {
        require(activityEnabled["assignment"], "Assignment rewards disabled");
        require(amount <= maxRewardPerActivity["assignment"], "Amount exceeds max reward");
        require(!hasReceivedReward[student]["assignment"][assignmentId], "Already rewarded for this assignment");

        hasReceivedReward[student]["assignment"][assignmentId] = true;
        token.mint(student, amount, "assignment");

        emit RewardDistributed(student, "assignment", assignmentId, amount, block.timestamp);
    }

    /**
     * @dev Distribute reward for writing a review
     */
    function distributeReviewReward(
        address student,
        uint256 reviewId,
        uint256 amount
    ) public onlyRole(REWARDER_ROLE) {
        require(activityEnabled["review"], "Review rewards disabled");
        require(amount <= maxRewardPerActivity["review"], "Amount exceeds max reward");
        require(!hasReceivedReward[student]["review"][reviewId], "Already rewarded for this review");

        hasReceivedReward[student]["review"][reviewId] = true;
        token.mint(student, amount, "review");

        emit RewardDistributed(student, "review", reviewId, amount, block.timestamp);
    }

    /**
     * @dev Distribute reward for forum participation
     */
    function distributeForumReward(
        address student,
        uint256 forumId,
        uint256 amount
    ) public onlyRole(REWARDER_ROLE) {
        require(activityEnabled["forum"], "Forum rewards disabled");
        require(amount <= maxRewardPerActivity["forum"], "Amount exceeds max reward");
        require(!hasReceivedReward[student]["forum"][forumId], "Already rewarded for this forum activity");

        hasReceivedReward[student]["forum"][forumId] = true;
        token.mint(student, amount, "forum");

        emit RewardDistributed(student, "forum", forumId, amount, block.timestamp);
    }

    /**
     * @dev Distribute reward for daily login streak
     */
    function distributeStreakReward(
        address student,
        uint256 streakDays,
        uint256 amount
    ) public onlyRole(REWARDER_ROLE) {
        require(activityEnabled["streak"], "Streak rewards disabled");
        require(amount <= maxRewardPerActivity["streak"], "Amount exceeds max reward");

        token.mint(student, amount, "streak");

        emit RewardDistributed(student, "streak", streakDays, amount, block.timestamp);
    }

    /**
     * @dev Update activity configuration (admin only)
     */
    function updateActivityConfig(
        string memory activityType,
        uint256 maxReward,
        bool enabled
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        maxRewardPerActivity[activityType] = maxReward;
        activityEnabled[activityType] = enabled;
        
        emit ActivityConfigUpdated(activityType, maxReward, enabled);
    }

    /**
     * @dev Check if user has received reward for a specific activity
     */
    function hasReceivedRewardFor(
        address student,
        string memory activityType,
        uint256 activityId
    ) public view returns (bool) {
        return hasReceivedReward[student][activityType][activityId];
    }
}

